mapper.select:
select * from product p inner join cate c on p.cate_id = c.cate_id


        p.product_name like concat('%',#{productName},'%')




          and p.product_price  >=  #{startPrice}





          and p.product_price  <=  #{endPrice}





          and c.cate_id  =  #{cateId}

